import { ADD_ELEMENT } from "../constants/action-types";

const initialState = {
    list: [],
    players: {
      1: { name: "John", pv: 25, pvMax: 110, mana: 15, manaMax: 40, id: 1 },
      2: { name: "Jack", pv: 110, pvMax: 120, mana: 40, manaMax: 50, id: 2 },
      3: { name: "Jessy", pv: 120, pvMax: 130, mana: 50, manaMax: 60, id: 3 },
      4: { name: "Jenny", pv: 130, pvMax: 140, mana: 60, manaMax: 70, id: 4 }
    },
    monster: {
      name: "Hamza",
      pv: 450,
      pvMax: 500
    }
  };

  const rootReducer = (state = initialState, action) => {
    if (action.type === ADD_ELEMENT) {
      return Object.assign({}, state, {
        list: state.list.concat(action.payload)
      })
    }
    return state;
  };

  export default rootReducer;
