const initialState = {
    players: {
      1: { name: "Dragon Knight", pv: 235, pvMax: 235, mana: 50, manaMax: 50, hasPlayed: false, id: 1 },
      2: { name: "Nightblade", pv: 110, pvMax: 110, mana: 40, manaMax: 40, hasPlayed: false, id: 2 },
      3: { name: "Templar", pv: 100, pvMax: 100, mana: 80, manaMax: 80, hasPlayed: false, id: 3 },
      4: { name: "Sorcerer", pv: 100, pvMax: 100, mana: 90, manaMax: 90, hasPlayed: false, id: 4 }
    },
    monster: {
      name: "Molag Bal",
      pv: 500,
      pvMax: 500
    }
  };

  const rootReducer = (state = initialState, action) => {

    if (action.type === 'hitMonster') {
      let damages = action.payload.damages
      let newState = {
        ...state,
        monster: {
          ...state.monster,
          pv: state.monster.pv - damages
        }
      }
      console.log('hitMonster: ', newState);
      return newState
    }

    if (action.type === 'hitPlayer') {
      let damages = action.payload.damages
      let currentPlayer = [action.payload.player]
      let newState = {
        ...state,
        players: {
          ...state.players,
          [currentPlayer]: {
            ...state.players[currentPlayer],
            pv: state.players[currentPlayer].pv - damages,
            hasPlayed: true
          }
        }
      }
      console.log('hitPlayer: ', newState);
      return newState
    }

    if (action.type === 'turnEnds') {
      if (state.players[1].hasPlayed === true && state.players[2].hasPlayed === true && state.players[3].hasPlayed === true && state.players[4].hasPlayed === true) {

        let reset = action.payload.reset
        let newState = {
          ...state,
          players: {
            ...state.players,
            [1]: {
              ...state.players[1],
              hasPlayed: reset
            },
            [2]: {
              ...state.players[2],
              hasPlayed: reset
            },
            [3]: {
              ...state.players[2],
              hasPlayed: reset
            },
            [4]: {
              ...state.players[2],
              hasPlayed: reset
            },
          }
        }
        console.log('turnEnds: ', newState);
        return newState
      } else {
        return state;
      }
    }

      return state;
  }

  export default rootReducer;
